import { useState } from 'react';
import './stylesheet.css';


function App() {

  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const weightAsNumber = Number(weight);
  const heightAsNumber = Number(height);


  return (
    <div>
      <h1 id="center">BMI Calculator</h1>
      <div>
      <label>
        Height(m):
        <input
          value={height}
          onChange={e => setHeight(e.target.value)}
          type="number"
        />
      </label>
      <label>
        Weight:
        <input
          value={weight}
          onChange={e => setWeight(e.target.value)}
          type="number"
        />
        </label>
        </div>
        <div>
        <button onClick={() => setWeight(weightAsNumber + 10)}>
          Gain 10 pounds
        </button>
        <button onClick={() => setWeight(weightAsNumber - 10)}>
          Lose 10 pounds
        </button>
        </div>

      {height !== '' &&
        <p>Your height is {heightAsNumber}.</p>
      }
      {weight > 0 &&
        <p>Your weight is {weightAsNumber}.</p>
      }
      {weight > 0 &&
        <p>Your BMI {weightAsNumber/((heightAsNumber/100)**2)}.</p>
      }
    </div>
  );
}

export default App;
